mapper.select:
SELECT categoryno, name, subname, seq, cnt, parentno 
FROM category
WHERE categoryno = #{categoryno}

SELECT categoryno, name, subname, seq, cnt, parentno, r FROM ( SELECT categoryno, name, subname, seq, cnt, parentno, rownum as r FROM ( SELECT categoryno, name, subname, seq, cnt, parentno FROM category
WHERE UPPER(name) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(subname) LIKE '%' || UPPER(#{word}) || '%'
ORDER BY seq ASC ) ) WHERE r > = #{start_num} AND r < = #{end_num}
