mapper.select:
SELECT c_no, c_name, c_subname, c_seq, c_cnt, c_parent 
FROM category
WHERE c_no = #{c_no}

SELECT c_no, c_name, c_subname, c_seq, c_cnt, c_parent, r FROM ( SELECT c_no, c_name, c_subname, c_seq, c_cnt, c_parent, rownum as r FROM ( SELECT c_no, c_name, c_subname, c_seq, c_cnt, c_parent FROM category
WHERE UPPER(c_name) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(c_subname) LIKE '%' || UPPER(#{word}) || '%'
ORDER BY c_seq ASC ) ) WHERE r > = #{start_num} AND r < = #{end_num}
