mapper.select:
SELECT n.noticeno, n.title, n.contents, n.rdate, n.views, m.nickname, r FROM ( SELECT noticeno, title, contents, rdate, views, memberno, rownum as r FROM notice ORDER BY noticeno DESC ) n JOIN member m ON n.memberno = m.memberno
WHERE ( UPPER(n.title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(n.contents) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(m.nickname) LIKE '%' || UPPER(#{word}) || '%' )
AND r > = #{start_num} AND r < = #{end_num}

SELECT noticeno, title, contents, rdate, views, memberno
FROM notice
WHERE noticeno = #{noticeno}

mapper.update:
UPDATE notice
SET views = views + 1
WHERE noticeno = #{noticeno}

UPDATE notice
SET title = #{title}, contents = #{contents}
WHERE noticeno = #{noticeno}
