mapper.select:
SELECT COUNT(id) as cnt
FROM member
WHERE id=#{id}

SELECT memberno, id, passwd, mname, tel, zipcode, address1, address2, mdate, grade
FROM member
WHERE memberno = #{memberno}

SELECT memberno, id, passwd, mname, tel, zipcode, address1, address2, mdate, grade
FROM member
WHERE id = #{id}

mapper.update:
UPDATE member 
SET mname=#{mname}, tel=#{tel}, zipcode=#{zipcode}, address1=#{address1}, address2=#{address2}
WHERE memberno=#{memberno}

mapper.select:
SELECT COUNT(memberno) as cnt
FROM member
WHERE id=#{id} AND passwd=#{passwd}

SELECT COUNT(memberno) as cnt
FROM member
WHERE memberno=#{memberno} AND passwd=#{passwd}

mapper.update:
UPDATE member
SET passwd=#{passwd}
WHERE memberno=#{memberno}

mapper.select:
SELECT COUNT(m_id) as cnt
FROM member
WHERE m_id=#{id}

SELECT m_no, m_id, m_pw, m_name, m_nickname, m_phone, m_email, m_thumb, m_addr1, m_addr2, m_zipcode, m_date, m_rdate, m_point, m_gender, m_grade, m_role
FROM member
WHERE m_id=#{id}
