mapper.insert:
DECLARE
    v_noticeno NUMBER;
BEGIN
    INSERT INTO notice (noticeno, title, contents, rdate, memberno)
    VALUES (NOTICE_SEQ.nextval, #{noticeVO.title}, #{noticeVO.contents}, SYSDATE, 1)
    RETURNING noticeno INTO v_noticeno;

    INSERT INTO notice_file (notice_file_no, name, sizes, ex, src, noticeno)
    VALUES (NOTICE_FILE_SEQ.nextval, #{name}, #{sizes}, #{ex}, #{src}, v_noticeno);

END;

mapper.select:
SELECT n.noticeno, n.title, n.contents, n.rdate, n.views, m.nickname, nf.notice_file_no, nf.name, nf.sizes, nf.ex, nf.src, r FROM ( SELECT noticeno, title, contents, rdate, views, memberno, rownum as r FROM notice ORDER BY noticeno DESC ) n JOIN member m ON n.memberno = m.memberno LEFT JOIN notice_file nf ON n.noticeno = nf.noticeno
WHERE ( UPPER(n.title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(n.contents) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(m.nickname) LIKE '%' || UPPER(#{word}) || '%' )
AND r > = #{start_num} AND r < = #{end_num}

 select count(*) as cnt
 FROM notice_file 
WHERE noticeno = #{noticeno}

mapper.update:
UPDATE notice
SET views = views + 1
WHERE noticeno = #{noticeno}

mapper.select:
select views
FROM notice 
WHERE noticeno = #{noticeno}

mapper.update:
DECLARE
    v_noticeno NUMBER;
BEGIN
    UPDATE notice 
    SET title = #{title}, 
    contents = #{contents}
    WHERE noticeno = #{noticeno};

    UPDATE notice_file 
    SET name = #{name}, 
    sizes = #{sizes}, 
    ex = #{ex}, 
    src = #{src} 
    WHERE noticeno = #{noticeno};

END;
