mapper.select:
SELECT payment_inquiry_no, title, contents, rdate, answer_visible, shoestitle, nickname FROM ( SELECT pi.payment_inquiry_no as payment_inquiry_no, pi.title as title, pi.contents as contents, pi.rdate as rdate, pi.answer_visible as answer_visible, s.title shoestitle, m.nickname as nickname, ROW_NUMBER() OVER( ORDER BY CASE WHEN answer_visible = 'N' THEN 0 ELSE 1 END, payment_inquiry_no DESC ) AS r FROM payment_inquiry pi JOIN member m ON pi.memberno = m.memberno JOIN payment p ON pi.paymentno = p.paymentno JOIN payment_details pd ON pd.paymentno = p.paymentno JOIN options o ON o.optionno = pd.optionno JOIN shoes s ON s.shoesno = o.shoesno
WHERE ( UPPER(pi.title) LIKE '%' || UPPER('a') || '%' OR UPPER(pi.contents) LIKE '%' || UPPER('a') || '%' OR UPPER(s.title) LIKE '%' || UPPER('a') || '%' OR UPPER(m.nickname) LIKE '%' || UPPER('a') || '%' )
) WHERE r >= #{start_num} AND r <= #{end_num}

mapper.update:
UPDATE shoes_inquiry
SET answer_visible = #{answer_visible}, answer_contents = #{answer_contents}
WHERE shoes_inquiry_no = #{shoes_inquiry_no}
