mapper.update:
UPDATE review
SET contents = #{contents}, rating = #{rating}
WHERE reviewno = #{reviewno}

mapper.select:
SELECT reviewno, contentes FROM ( SELECT r.reviewno AS reviewno, r.contents AS contents, ROW_NUMBER() OVER( ORDER BY CASE WHEN s.visible = 'N' THEN 0 ELSE 1 END, r.reviewno DESC ) AS r FROM review r JOIN shoes s ON r.memberno = s.memberno JOIN hates h ON s.memberno = h.memberno
WHERE cl.reviewno = #{reviewno}
AND ( UPPER(r.contents) LIKE '%' || UPPER(#{word}) || '%' )
WHERE ( UPPER(r.contents) LIKE '%' || UPPER(#{word}) || '%' )
)

SELECT reviewno, contents, rating, rdate
FROM review
WHERE reviewno = #{reviewno}
