mapper.select:
SELECT SHOESNO, MEMBERNO, TITLE, BRAND, RATING, PRICE, DISCOUNT, CONTENTS, VISIBLE
FROM SHOES
WHERE shoesno=#{shoesno}

SELECT OPTIONNO, SIZES, AMOUNT, COLOR, SHOESNO
FROM options 
WHERE shoesno =#{shoesno} AND optionno = #{optionno}

  SELECT o.optionno, o.shoesno, o.sizes, o.amount, o.color, r
  FROM (
    SELECT optionno, shoesno, sizes, amount, color, rownum as r
    FROM options
    WHERE shoesno = #{shoesno}
  ) o  
JOIN shoes s ON o.shoesno = s.shoesno
WHERE r > = #{start_num} AND r < = #{end_num}

mapper.update:
UPDATE SHOES
SET TITLE = #{title}, BRAND = #{brand}, RATING = #{rating}, PRICE =#{price}, DISCOUNT = #{discount}, CONTENTS = #{contents}, VISIBLE = #{visible}
WHERE SHOESNO = #{shoesno}

UPDATE OPTIONS
SET SIZES = #{sizes}, AMOUNT = #{amount}, COLOR = #{color}
WHERE OPTIONNO = #{optionno}

mapper.select:
SELECT SHOESNO, MEMBERNO, TITLE, BRAND, RATING, PRICE, DISCOUNT, CONTENTS, VISIBLE, r FROM ( SELECT SHOESNO, MEMBERNO, TITLE, BRAND, RATING, PRICE, DISCOUNT, CONTENTS, VISIBLE, rownum as r FROM ( SELECT SHOESNO, MEMBERNO, TITLE, BRAND, RATING, PRICE, DISCOUNT, CONTENTS, VISIBLE FROM SHOES
WHERE UPPER(TITLE) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(BRAND) LIKE '%' || UPPER(#{word}) || '%'
ORDER BY shoesno ASC ) ) WHERE r > = #{start_num} AND r < = #{end_num}

SELECT REVIEWNO, CONTENTS, GRADE, RDATE, SHOESNO, MEMBERNO, r FROM ( SELECT REVIEWNO, CONTENTS, GRADE, RDATE, SHOESNO, MEMBERNO, rownum as r FROM ( SELECT R.REVIEWNO, R.CONTENTS, R.GRADE, R.RDATE, R.SHOESNO, R.MEMBERNO FROM REVIEW R INNER JOIN SHOES S ON R.SHOESNO = S.SHOESNO
WHERE UPPER(R.CONTENTS) LIKE '%' || UPPER(#{word}) || '%'
ORDER BY R.REVIEWNO ASC ) ) WHERE r >= #{start_num} AND r <= #{end_num}
