mapper.update:
UPDATE SHOES
SET TITLE = #{title}, BRAND = #{brand}, RATING = #{rating}, PRICE =#{price},
DISCOUNT = #{discount}, CONTENTS = #{contents}, VISIBLE = #{visible}
WHERE SHOESNO = #{shoesno}

mapper.select:
SELECT shoesno, title, brand, contents, price, visible FROM ( SELECT s.shoesno AS shoesno, s.title AS title, s.brand AS brand, s.contents AS contents, s.price AS price, s.visible AS visible, ROW_NUMBER() OVER( ORDER BY CASE WHEN s.visible = 'N' THEN 0 ELSE 1 END, s.shoesno DESC ) AS r FROM shoes s JOIN category_list cl ON s.shoesno = cl.shoesno JOIN category c ON cl.categoryno = c.categoryno
WHERE cl.categoryno = #{categoryno}
AND ( UPPER(s.title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(s.brand) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(s.contents) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(s.price) LIKE '%' || UPPER(#{word}) || '%' )
WHERE ( UPPER(s.title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(s.brand) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(s.contents) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(s.price) LIKE '%' || UPPER(#{word}) || '%' )
)
