mapper.select:
SELECT shoes_inquiry_no, title, contents, rdate, answer_visible, shoestitle, nickname FROM ( SELECT si.shoes_inquiry_no, si.title, si.contents, si.rdate, si.answer_visible, s.title AS shoestitle, m.nickname, ROW_NUMBER() OVER( ORDER BY CASE WHEN answer_visible = 'N' THEN 0 ELSE 1 END, shoes_inquiry_no DESC ) AS r FROM shoes_inquiry si JOIN member m ON si.memberno = m.memberno JOIN options o ON si.optionno = o.optionno JOIN shoes s ON o.shoesno = s.shoesno
WHERE ( UPPER(si.title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(si.contents) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(s.title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(m.nickname) LIKE '%' || UPPER(#{word}) || '%' )
) WHERE r >= #{start_num} AND r <= #{end_num}
